INSERT INTO `wp_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `icon`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`) VALUES
(1, 'wc-admin-welcome-note', 'info', 'en_US', 'Nouvelle(s) fonctionnalité(s)', 'Bienvenue dans la nouvelle expérience WooCommerce ! Cette nouvelle version vous offrira un aperçu de votre boutique dans le tableau de bord. Elle vous permettra également de gérer des aspects importants de votre activité (commandes, stock, avis, etc.) depuis n\'importe quelle partie de l\'interface et d\'explorer les données de votre boutique au moyen d\'une nouvelle section de statistiques et plus encore !', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-04-29 13:35:09', NULL, 0),
(2, 'wc-admin-store-notice-setting-moved', 'info', 'en_US', 'Vous recherchez le paramètre Annonce de la boutique ?', 'Vous le trouverez désormais dans l\'outil de personnalisation.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-04-29 13:35:09', NULL, 0),
(3, 'wc-admin-add-first-product', 'info', 'en_US', 'Ajouter votre premier produit', 'Augmentez vos revenus en ajoutant des produits à votre boutique. Ajoutez des produits manuellement, importez-les à partir d\'une feuille de calcul ou migrez-les à partir d\'une autre plateforme', 'product', '{}', 'unactioned', 'woocommerce-admin', '2020-04-29 13:35:10', NULL, 0),
(4, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Se connecter à WooCommerce.com', 'Connectez-vous pour obtenir des notifications et des mises à jour importantes sur les produits.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-04-29 13:35:10', NULL, 0),
(5, 'wc-admin-onboarding-profiler-reminder', 'update', 'en_US', 'Bienvenue dans WooCommerce ! Configurer votre boutique et commencer à vendre', 'Notre objectif est de vous aider tout au long des étapes les plus importantes du lancement de votre boutique.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-04-29 13:35:20', NULL, 0),
(6, 'wc-admin-mobile-app', 'info', 'en_US', 'Installer l\'application mobile Woo', 'Installez l\'application mobile WooCommerce pour gérer les commandes, recevoir des notifications de vente et afficher les statistiques principales où que vous soyez.', 'phone', '{}', 'unactioned', 'woocommerce-admin', '2020-05-01 13:43:39', NULL, 0),
(7, 'wc-admin-store-notice-giving-feedback', 'info', 'en_US', 'Donner votre avis sur votre expérience', 'Si vous aimez WooCommerce Admin, n\'hésitez pas à nous laisser une note de 5 étoiles. Un grand merci d\'avance !', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-02 13:35:21', NULL, 0),
(8, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Aider WooCommerce à améliorer ses services grâce au suivi de l\'utilisation', 'Récupérer les données d\'utilisation nous permet d\'améliorer WooCommerce. Votre boutique sera prise en compte lors de l\'évaluation des nouvelles fonctionnalités et de la qualité d\'une mise à jour, ou pour déterminer l\'intérêt d\'une amélioration. Vous pouvez toujours consulter les <a href=\"http://localhost/newready/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Paramètres</a> et choisir d\'interrompre le partage des données. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">En savoir plus</a> sur les données que nous collectons.', 'info', '{}', 'unactioned', 'woocommerce-admin', '2020-05-22 14:15:32', NULL, 0);
